//reducer
import {combineReducers} from 'redux';


const initialUserState = {
    firstName : '',
    lastName : '',
    userName : '',
    email : '',
    picture : ''
};

const initialQuestionState = {
    questionID : '',
    question : '',
    optionOne : '',
    optionTwo : '',
    optionThree : '',
    optionFour : '',
    correctOption : ''
};


const ADD_USER = 'ADD_USER';
const ADD_QUESTION = 'ADD_QUESTION';

//reducer initial_states are immutable by principle.
//As a consequence we use 'Object.assign' for returning a new object taking properties from multiple sources rather than modifying the immutable one.

const userStateReducer = (state = initialUserState, action) => {
    if (action.type === ADD_USER)  {
        return (Object.assign({}, state, action.payload));
    }
};

const questionStateReducer = (state = initialQuestionState, action) => {
    if (action.type === ADD_QUESTION) {
        return (action.payload);
    }
}

export default combineReducers({userStateReducer, questionStateReducer}) ;
