import React from 'react';
import {Link} from 'react-router-dom';
import {connect} from 'react-redux';
import './playComponents/playStart.css';
import DynamicGameArea from './playComponents/dynamicGameArea';
import {mapStateToProps} from '../utils/mapReduxToState';



const ConnectedPlay = (props) => {
    return(
        <div className={'play'}>
            <div className={'navBar'}>
                <Link to={'/'} className={'navBar-logo'}>
                    <span>Salvos</span>
                </Link>
                <span className={'helloName'}>
                    {(props.state.firstName)? `Hello, ${props.state.firstName}` : `quizNinja`}
                </span>
            </div>
            <DynamicGameArea className={'dynamicGameArea'}/>
        </div>
    );
};

const Play = connect(mapStateToProps)(ConnectedPlay);
export default Play;
