import React, {useState} from 'react';
import './playStart.css';
import '../homeComponents/home.css';


const GameQuestionRenderer = (props) => {
    const questionObject = props.stateData.questions[props.stateData.progress];

    return (
            <div>
                <p className={'questionText'}>{questionObject.question}</p>
                {(questionObject.imagePath) ? <img src='' className={'questionImage'} /> : null}
                <div className={'questionOptions'}>
                    <div className={'optionInstant'} onCliclk={() => {}}>{questionObject.optionOne}</div>
                    <div className={'optionInstant'} onClick={() => {}}>{questionObject.optionTwo}</div>
                    <div className={'optionInstant'} onClick={() => {}}>{questionObject.optionThree}</div>
                    <div className={'optionInstant'} onClick={() => {}}>{questionObject.optionFour}</div>
                </div>
            </div>
    );
};



const GameScreen = (props) => {
    return(
        <div className={'gameAreaQuestionRender'}>
            <h2 className={'questionTitle'}>Question: </h2>
            <GameQuestionRenderer props={props.stateData} />
        </div>
    );
};



const DynamicGameArea = (props) => {
    const [startClicked, setStartClicked] = useState(false);
    if (!startClicked){
        return (
            <div className={'gameArea'}>
                <p className={'start-text'}>You are ready to go</p>
                <div className={'start-underLine'}></div>
                <div className={'button start-button'} onClick={() => setStartClicked(true)}>Start Game</div>
            </div>
       );
    }
    return <GameScreen props={props.stateData}/>;
};


export default DynamicGameArea;
