import React, {useEffect, useState} from 'react';
import {useMutation, useQuery, useLazyQuery} from '@apollo/react-hooks';
import gql from 'graphql-tag';
import Cookies from 'js-cookie';
import './playStart.css';
import '../homeComponents/home.css';



const ADD_RESPONSE = gql`
    mutation($ID : String!, $response : Int!, $questionID : Int!)
    {
        addRespose(ID : $ID, response : $response, progress : $questionID)
        {
            ID,
            response,
            progress
        }
    }
`;


const GameQuestionRenderer = (props) => {
    const [questionTimer, setQuestionTimer] = useState(20);
    const [sendResponse, {data, called, error}] = useMutation(ADD_RESPONSE);
    const classOptionOne = {};
    const classOptionTwo = {};
    const classOptionThree = {};
    const classOptionFour = {};
    const ID = Cookies.get('user_id');

    useEffect(() => {
        if (questionTimer > 0){
            setInterval(() => {setQuestionTimer(questionTimer-1)}, 1000);
        }
    }, [questionTimer]);

    const checkAnswer = (response, responseText, questionID) => {
        if (!called){
            sendResponse({
                variables : {ID, progress : questionID, response}
            }).then(() => console.log('response_sent')).catch((err) => console.log(err));
        }
        if (error){
            console.log(error);
        }

        switch (response) {
            case 1:
                if (responseText === props.question.correctOption) {
                    classOptionOne.backgroundColor = 'green';
                }
                else {
                    classOptionOne.backgroundColor = 'red';
                }
                break;
            case 2:
                if (responseText === props.question.correctOption) {
                    classOptionTwo.backgroundColor = 'green';
                }
                else {
                    classOptionTwo.backgroundColor = 'red';
                }
                break;
            case 3:
                if (responseText === props.question.correctOption) {
                    classOptionThree.backgroundColor = 'green';
                }
                else {
                    classOptionThree.backgroundColor = 'red';
                }
                break;
            case 4:
                if (responseText === props.question.correctOption) {
                    classOptionFour.backgroundColor = 'green';
                }
                else {
                    classOptionFour.backgroundColor = 'red';
                }
                break;
        }
    };
    return (
            <div>
                <div className={'questionHeader'}>
                    <h2 className={'questionTitle'}>Question: </h2>
                    <div className={'questionTimer'}>{questionTimer}</div>
                </div>
                <p className={'questionText'}>{props.question.question}</p>
                {(props.question.imagePath) ? <img src={props.question.imagePath} className={'questionImage'} /> : null}
                <div className={'questionOptions'}>
                    <div style={classOptionOne} onCliclk={() => checkAnswer(1, props.question.optionOne, props.question.questionID)}>{props.question.optionOne}</div>
                    <div style={classOptionTwo} onClick={() => checkAnswer(2, props.question.optionTwo, props.question.questionID)}>{props.question.optionTwo}</div>
                    <div style={classOptionThree} onClick={() => checkAnswer(3, props.question.optionThree, props.question.questionID)}>{props.question.optionThree}</div>
                    <div style={classOptionFour} onClick={() => checkAnswer(4, props.question.optionFour, props.question.questionID)}>{props.question.optionFour}</div>
                </div>
            </div>
    );
};


const TimeOut = (props) => {
    const [sendResponse] = useMutation(ADD_RESPONSE);
    const response = 0;
    const ID = Cookies.get('user_id');

    sendResponse({
        variables : {ID, progress : props.questionID, response}
    }).then(() => console.log('response_sent')).catch((err) => console.log(err));

    return (
        <div className={'timeOut'}>
            <span className={'timeOutHead'}>Time Out..!!</span>
        </div>
    );
};


const QuizCompleted = () => {
    return (
        <div className={'quizCompleted'}>
            <p>You have completed the quiz</p>
        </div>
    );
};


const GameScreen = (props) => {
    const [progress, setProgress] = useState(props.stateData.progress);
    const [question, setQuestion] = useState(props.stateData.questions[progress]);
    const [switchToTimeOut, setSwitchToTimeOut] = useState(false);

    useEffect(() => {
        setInterval(() => {
            setSwitchToTimeOut(true);
        }, 20000);
    }, [question, switchToTimeOut]);


    if (switchToTimeOut){
        if (progress <= props.stateData.questions.length - 1){
            setInterval(() => {
                setProgress(progress + 1);
                setQuestion(props.stateData.questions[progress]);
                setSwitchToTimeOut(false);
            }, 4000);
            return <TimeOut questionID={question.questionID} />
        }
        else {
            return <QuizCompleted />
        }
    }
    if (!switchToTimeOut){
        return <GameQuestionRenderer question={question}/>
    }
};


const DynamicGameArea = (props) => {
    const [startClicked, setStartClicked] = useState(false);
    if (!startClicked){
        return (
            <div className={'gameArea'}>
                <p className={'start-text'}>You are ready to go</p>
                <div className={'start-underLine'}></div>
                <div className={'button start-button'} onClick={() => setStartClicked(true)}>Start Game</div>
            </div>
       );
    }
    return <GameScreen stateData = {props.stateData} />;
};


export default DynamicGameArea;
