import React, {useState} from 'react';
import {Redirect} from 'react-router-dom';
import './playStart.css';
import {graphql} from 'react-apollo';
import { useQuery } from '@apollo/react-hooks';
import gql from 'graphql-tag';
import '../homeComponents/home.css';
import {connect} from 'react-redux';
import Cookies from 'js-cookie';
import {mapQuestionsDispatchToProps, mapUserDispatchToProps, mapUserStateToProps, mapQuestionsStateToProps} from "../../utils/mapReduxToState";


const getQuery = gql`
    query
    {
        user(id : $ID)
        {
            userName,
            firstName,
            lastName,
            picture,
            email,
            progress
        },
        questions
        {
            questionID,
            question,
            optionOne,
            optionTwo,
            optionThree,
            optionFour,
            correctOption
        }
    }
`;
const ConnectedGameScreen = (props) => {
    console.log(data);
    return(
        <div className={'gameArea-question-render'}>
            <h2 className={'question-title'}>Question: </h2>
        </div>
    );
};

const GameScreen = connect(mapQuestionsStateToProps)(ConnectedGameScreen);

const ConnectedDynamicGameArea = (props) => {
    const [startClicked, setStartClicked] = useState(false);
    const ID = Cookies.get('user_id') || null;
    const {loading, error, data} = useQuery(getQuery, {
        variables : {ID}
    });
    if (ID){
        console.log(data);
        if (!startClicked){
            return (
                <div className={'gameArea'}>
                    <p className={'start-text'}>You are ready to go</p>
                    <div className={'start-underLine'}></div>
                    <div className={'button start-button'} onClick={() => setStartClicked(true)}>Start Game</div>
                </div>
            );
        }
        return <GameScreen ID={ID}/>;
    }
    else {
        return (
            <Redirect to={'/'} />
        );
    }

};

const DynamicGameArea = connect(mapUserStateToProps, mapUserDispatchToProps)(ConnectedDynamicGameArea);
export default DynamicGameArea;
