import React, {useEffect, useState} from 'react';
import './playQuestion.css';

const OptionInstant = (props) => {
    const [optionText, setOptionText] = useState(props.optionText);
    useEffect(() => {
        props.disableOptions(false);
        props.setAnswered(false);
    }, [optionText]);
	return (
        <li className={'optionInstant'}>
            <button className={'optionButton'} disabled={props.disabled} onClick={() => {
                console.log('button clicked...');
                props.disableOptions(true);
                props.setAnswered(true);
                props.checkAnswer(optionText);
            }}>
                {optionText}
            </button>
        </li>
    );
};

export default OptionInstant;
