import React from 'react';
import {Link} from 'react-router-dom';
import {connect} from 'react-redux';
import './playComponents/playStart.css';
import DynamicGameArea from './playComponents/dynamicGameArea';
import { useQuery } from '@apollo/react-hooks';
import gql from 'graphql-tag';
import Cookies from 'js-cookie';


const getQuery = gql`
    query ($ID : String!)
    {
        user(ID : $ID)
        {
            userName,
            firstName,
            lastName,
            picture,
            email,
            progress
        },
        questions
        {
            questionID,
            question,
            optionOne,
            optionTwo,
            optionThree,
            optionFour,
            correctOption
        }
    }
`;

let stateData;

const Play = (props) => {
    const ID = Cookies.get('user_id');
    const {loading, error, data} = useQuery(getQuery, {
        variables : {ID}
    });
    if (error) {
        return  (<p>{`Error occured: ${error}`}</p>);
    }
    if (loading) {
        return  (<p>loading...</p>);
    }
    stateData = {...data.user};
    stateData.questions = data.questions;
    console.log('length of questions ' + stateData.questions);
    return(
        <div className={'play'}>
            <div className={'navBar'}>
                <Link to={'/'} className={'navBar-logo'}>
                    <span>Salvos</span>
                </Link>
                <span className={'helloName'}>
                    {`Hello, ${stateData.firstName || `...`}`}
                </span>
            </div>
            <DynamicGameArea className={'dynamicGameArea'} stateData={stateData} />
        </div>
    );
};

export default Play;
