import React from 'react';
import './playQuestion.css';

const OptionInstant = (props) => {
    return (
        <li className={'optionInstant'}>
            <button className={'optionButton'} onClick={() => props.checkAnswer(props.optionText)}>
                {props.optionText}
            </button>
        </li>
    );
};

export default OptionInstant;
