import gql from 'graphql-tag';

export const GET_QUERY = gql`
    query ($ID : String!)
    {
        user(ID : $ID)
        {
            userName,
            firstName,
            lastName,
            progress,
            points
        },
        questions
        {
            questionID,
            question,
            optionOne,
            optionTwo,
            time,
            optionThree,
            optionFour,
            correctOption
        }
    }
`;

export const ADD_RESPONSE = gql`
    mutation AddResponse($ID: String!, $progress: Int!, $response : String!) {
        addResponse(ID: $ID, progress: $progress, response: $response, points : $points) {
            progress
        }
    }
`;
