import React from 'react';

const OptionInstant = (props) => {
    return (
        <li className={'optionInstant'}>
            <input type={'radio'}
                   className={'optionRadio'}
                   onChange={(e) => props.checkAnswer(e)}
                   value={props.optionText} />
            <label className={'optionLabel'}>{props.optionText}</label>
        </li>
    );
};

export default OptionInstant;
